update `order` set price=(
select SUM(oio.price) as sumPrice from(
select oi.unitPrice*oi.num as price from `order` o RIGHT JOIN `orderitem` oi on o.order_id=oi.order_id WHERE o.order_id=#{orderId,jdbcType=VARCHAR}
)oio
) WHERE order_id=#{orderId,jdbcType=VARCHAR}

update `order`  set
  score=#{score,jdbcType=INTEGER},
  sendscore=#{sendscore,jdbcType=INTEGER}
where order_id=#{orderId,jdbcType=VARCHAR}
